  .withAutomaticReconnect()
  .build();

function App() {
  useEffect(() => {
    connection.start().then(() => {
      connection.invoke("Subscribe", userId);
    });
    connection.on("SendNotification", (message) => {
      // показать toast
    });
  }, []);

  return (
    <div>
      <h1>Hello world</h1>
    </div>
  )
}

export default App
